import React from "react";
import { MdSettings } from "react-icons/md";
import { useState } from "react";
import { Offcanvas } from "react-bootstrap";
import Swatch from "./Swatch";

export default function Settings() {
  const [show, setShow] = useState(false);
  function toggleShow() {
    setShow(true);
  }
  function handleClose() {
    setShow(false);
  }
  const styles = {
    backgroundColor: "grey",
  };
  const array = [
      { title: "default", styles:{ backgroundColor: "grey" } },
      { title: "default", styles:{ backgroundColor: "grey" } },
      { title: "default", styles:{ backgroundColor: "grey" } },
      { title: "default", styles:{ backgroundColor: "grey" } },
      { title: "default", styles:{ backgroundColor: "grey" } },
];

  return (
    <>
      <button className="settings" onClick={toggleShow}>
        <MdSettings className="spin" />
      </button>
      <Offcanvas
        show={show}
        onHide={handleClose}
        name="Enable body scrolling"
        scroll={true}
        backdrop={false}
        placement="end"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>theme customizer</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <span className="sub-h">Try a pre-built swatch!</span>
          <Swatch style={array[0].styles} title={array[0].title}/>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}
