import React from "react";
import { MdSettings } from "react-icons/md";
import { useState } from "react";
import { Offcanvas } from "react-bootstrap";
import Swatch from "./Swatch";

export default function Settings() {
  const [show, setShow] = useState(false);
  function toggleShow() {
    setShow(true);
  }
  function handleClose() {
    setShow(false);
  }

  const array1 = [
    {
      title: "default",
      styles: {
        backgroundColor: "#0061f2 ",
        backgroundImage: "linear-gradient(45deg,#0061f2,#6900c7)",
      },
    },
    {
      title: "flat",
      styles: {
        backgroundColor: "#1da1f5 ",
        backgroundImage: "linear-gradient(45deg,#1da1f5,#8039da)",
      },
    },
    {
      title: "sunset",
      styles: {
        backgroundColor: "#f53b57 ",
        backgroundImage: "linear-gradient(45deg,#f53b57,#ff793f)",
      },
    },
    {
      title: "subtle",
      styles: {
        backgroundColor: "#6eabc2 ",
        backgroundImage: "linear-gradient(45deg,#6eabc2,#506c6a)",
      },
    },
    {
      title: "seafoam",
      styles: {
        backgroundColor: "#06794f ",
        backgroundImage: "linear-gradient(45deg,#06794f,#0fa28b)",
      },
    },
  ];
  const array2 = [
    {
      title: "primary",
      hash:"#1da1f5",
      styles: {
        backgroundColor: "#1da1f5 ",
        borderColor: "#1da1f5",
      },
    },
    {
      title: "secondary",
      hash:"#8039da",
      styles: {
        backgroundColor: "#8039da ",
        borderColor: "#8039da",
      },
    },
    {
      title: "success",
      hash:"#12ac70",
      styles: {
        backgroundColor: "#12ac70 ",
        borderColor: "#12ac70",
      },
    },
    {
      title: "danger",
      hash:"#e83d2c",
      styles: {
        backgroundColor: "#e83d2c ",
        borderColor: "#e83d2c",
      },
    },
    {
      title: "warning",
      hash:"#ffc147",
      styles: {
        backgroundColor: "#ffc147 ",
        borderColor: "#ffc147",
      },
    },
    {
      title: "info",
      hash:"#2ccfd4",
      styles: {
        backgroundColor: "#2ccfd4 ",
        borderColor: "#2ccfd4",
      },
    },

  ];
  return (
    <>
      <button className="settings" onClick={toggleShow}>
        <MdSettings className="spin" />
      </button>
      <Offcanvas
        show={show}
        onHide={handleClose}
        name="Enable body scrolling"
        scroll={true}
        backdrop={false}
        placement="end"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>theme customizer</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <span className="sub-h mt-3">Try a pre-built swatch!</span>
          {array1.map((array) => {
            return <Swatch style={array.styles} title={array.title} />;
          })}
          <br />
          <span className="sub-h mt-3">Or choose your own colors</span>
          {array2.map((array) => {
            return <Swatch style={array.styles} title={array.title} hash={array.hash}/>;
          })}
          <span className="sub-h mt-3">Need ideas? Randomize!</span>
          <Swatch title="randomize colors" style={{color:"black",backgroundColor:"white", borderColor:"black"}}></Swatch>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}
