import React from "react";
import { MdSettings } from "react-icons/md";
import { useState } from "react";
import { Offcanvas } from "react-bootstrap";
import Swatch from "./Swatch";

export default function Settings() {
  const [show, setShow] = useState(false);
  function toggleShow() {
    setShow(true);
  }
  function handleClose() {
    setShow(false);
  }

  const array = [
      { title: "default", styles:{ backgroundColor: "#0061f2 ",backgroundImage:"linear-gradient(45deg,#0061f2,#6900c7)"} },
      { title: "default", styles:{ backgroundColor: "#1da1f5 ",backgroundImage:"linear-gradient(45deg,#1da1f5,#8039da)"} },
      { title: "default", styles:{ backgroundColor: "#f53b57 ",backgroundImage:"linear-gradient(45deg,#0061f2,#6900c7)"} },
      { title: "default", styles:{ backgroundColor: "#0061f2 ",backgroundImage:"linear-gradient(45deg,#0061f2,#6900c7)"} },
      { title: "default", styles:{ backgroundColor: "#0061f2 ",backgroundImage:"linear-gradient(45deg,#0061f2,#6900c7)"} },

];

  return (
    <>
      <button className="settings" onClick={toggleShow}>
        <MdSettings className="spin" />
      </button>
      <Offcanvas
        show={show}
        onHide={handleClose}
        name="Enable body scrolling"
        scroll={true}
        backdrop={false}
        placement="end"
      >
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>theme customizer</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <span className="sub-h mt-3">Try a pre-built swatch!</span>
          {array.map(array=>{return <Swatch style={array.styles} title={array.title} />})}
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}
